queries:
PREFIX skos: <http://www.w3.org/2004/02/skos/core#>
PREFIX dc: <http://purl.org/dc/elements/1.1/>

SELECT DISTINCT ?scheme ?term ?label
WHERE {
  { # vocabulary info
    ?scheme a skos:ConceptScheme; dc:title ?label.
    FILTER(?scheme IN (
    <https://dev.lod.coop/essglobal/2.1/standard/activities-modified/>
    ))
  }
  UNION
  { # term info
    ?term
       a skos:Concept;
       skos:inScheme ?scheme.
    OPTIONAL { ?term skos:prefLabel ?label. }
    { ?term skos:inScheme <https://dev.lod.coop/essglobal/2.1/standard/activities-modified/> }
    FILTER (langMatches(lang(?label), "en"))
  }
}
